import React, { useEffect, useState } from 'react'

function Test() {
    const [slids, setSlides] = useState([{
        id:1,
        url: "data:image/png;base64,",
        title: "Slide Title 1",
        desc: "Some text 1"
    }, {
        id:2,
        url: "data:image/png;base64,",
        title: "Slide Title 2",
        desc: "Some text 2"
    }, {
        id:3,
        url: "data:image/png;base64,",
        title: "Slide Title 3",
        desc: "Some text 3"
    }]);
    
    const [activeSlide, setActiveSlide] = useState(1)
    const [activeSlideData, setActiveSlideData] = useState(1)  
    const pevSlide = () => {
        var nextSlideCount = activeSlide - 1
        if (nextSlideCount >= 1) {
            setActiveSlide(perv => perv - 1)
        }
    }
    const nextSlide = () => {
        var nextSlideCount = activeSlide + 1
        if (nextSlideCount <= slids.length) {
            setActiveSlide(perv => perv + 1)
        }
    }
    useEffect(() => {
        setActiveSlideData(() => slids.filter(slid=>slid.id == activeSlide)['0'])
    }, [activeSlide])    
  return (
    <div>        
        <div className='slide'><img src={activeSlideData?.url} /><h4>{activeSlideData?.title}</h4><p>{activeSlideData?.desc}</p>
        </div>
        <button onClick={() => pevSlide()} className='p-3 b-1 mr-4'>Pev</button>
        <button onClick={() => nextSlide()} className='p-3 b-1'>Next</button>
    </div>
  )
}

export default Test